func TestRepo_cleanIdentityFromConfig(t *testing.T) {
	var (
		dir            = path.Join(os.TempDir(), "openbazaar", "cleantest")
		configFilePath = path.Join(dir, "config")
	)

	if err := os.MkdirAll(dir, os.ModePerm); err != nil {
		t.Fatal(err)
	}
	defer os.RemoveAll(dir)

	if err := ioutil.WriteFile(configFilePath, []byte(`{"Identity": "abc"}`), os.ModePerm); err != nil {
		t.Fatal(err)
	}

	if err := cleanIdentityFromConfig(dir); err != nil {
		t.Fatal(err)
	}

	cfg, err := ioutil.ReadFile(configFilePath)
	if err != nil {
		t.Fatal(err)
	}

	if !bytes.Equal(cfg, []byte("{}")) {
		t.Error("Failed to properly clean config file")
	}
}

var expectedConfigJSON = `{"Identity":{"PeerID":"QmRVbfRMxkTFUttgmEL7yxeLFMrbexzyKK6PLrkS5azHwP","PrivKey":""},"Datastore":{"StorageMax":"10GB","StorageGCWatermark":90,"GCPeriod":"1h","Spec":{"mounts":[{"child":{"path":"blocks","shardFunc":"/repo/flatfs/shard/v1/next-to-last/2","sync":true,"type":"flatfs"},"mountpoint":"/blocks","prefix":"flatfs.datastore","type":"measure"},{"child":{"compression":"none","path":"datastore","type":"levelds"},"mountpoint":"/","prefix":"leveldb.datastore","type":"measure"}],"type":"mount"},"HashOnRead":false,"BloomFilterSize":0},"Addresses":{"Swarm":["/ip4/0.0.0.0/tcp/4001","/ip6/::/tcp/4001","/ip4/0.0.0.0/tcp/9005/ws","/ip6/::/tcp/9005/ws"],"Announce":null,"NoAnnounce":null,"API":"","Gateway":"/ip4/127.0.0.1/tcp/4002"},"Mounts":{"IPFS":"/ipfs","IPNS":"/ipns","FuseAllowOther":false},"Discovery":{"MDNS":{"Enabled":false,"Interval":10}},"Routing":{"Type":"dht"},"Ipns":{"RecordLifetime":"720h","ResolveCacheSize":128},"Bootstrap":[],"Gateway":{"HTTPHeaders":{"Access-Control-Allow-Headers":["X-Requested-With","Range","User-Agent"],"Access-Control-Allow-Methods":["GET"],"Access-Control-Allow-Origin":["*"]},"RootRedirect":"","Writable":false,"PathPrefixes":[],"APICommands":[],"NoFetch":false},"API":{"HTTPHeaders":{}},"Swarm":{"AddrFilters":null,"DisableBandwidthMetrics":false,"DisableNatPortMap":false,"DisableRelay":false,"EnableRelayHop":false,"EnableAutoRelay":true,"EnableAutoNATService":false,"ConnMgr":{"Type":"basic","LowWater":600,"HighWater":900,"GracePeriod":"20s"}},"Pubsub":{"Router":"","DisableSigning":false,"StrictSignatureVerification":false},"Reprovider":{"Interval":"12h","Strategy":"all"},"Experimental":{"FilestoreEnabled":false,"UrlstoreEnabled":false,"ShardingEnabled":false,"Libp2pStreamMounting":false,"P2pHttpProxy":false,"QUIC":false,"PreferTLS":false}}`

func TestRepo_mustDefaultConfig(t *testing.T) {
	cfg := mustDefaultConfig()
	// The Strings type screws up the json serialization
	// so we'll just set it to nil here to pass the test.
	// It should always be nil anyway.
	cfg.Addresses.API = nil

	expectedConfig := &config.Config{}
	if err := json.Unmarshal([]byte(expectedConfigJSON), expectedConfig); err != nil {
		t.Fatal(err)
	}
	expectedConfig.Identity = cfg.Identity
	expectedConfig.Addresses.API = nil
	expectedConfig.DNS.Resolvers = make(map[string]string)
	expectedConfig.Pinning.RemoteServices = make(map[string]config.RemotePinningService)
	expectedConfig.Migration.DownloadSources = []string{}

	if !reflect.DeepEqual(expectedConfig, cfg) {
		t.Error("Returned incorrect config file")
	}
}

func TestNewRepo(t *testing.T) {
	var dir = path.Join(os.TempDir(), "openbazaar", "newRepoTest")
	r, err := NewRepo(dir)
	if err != nil {
		t.Fatal(err)
	}
	defer r.DestroyRepo()

	if r.DB() == nil {
		t.Error("Failed to initialize the database")
	}
}

func TestNewRepoWithCustomMnemonicSeed(t *testing.T) {
	var (
		dir      = path.Join(os.TempDir(), "openbazaar", "newRepoTest")
		mnemonic = "abc"
	)
	r, err := NewRepoWithCustomMnemonicSeed(dir, mnemonic)
	if err != nil {
		t.Fatal(err)
	}
	defer r.DestroyRepo()

	var dbSeed models.Key
	err = r.db.View(func(tx database.Tx) error {
		return tx.Read().Where("name = ?", "mnemonic").First(&dbSeed).Error
	})
	if err != nil {
		t.Fatal(err)
	}

	if string(dbSeed.Value) != mnemonic {
		t.Errorf("Failed to set correct mnemonic. Expected %s, got %s", mnemonic, string(dbSeed.Value))
	}
}
